'use strict';
angular.module('DrewPersonalSiteApp',['stateRouter', 'MainHeader']);   
angular.module('stateRouter',['ui.router'])
.config(function($stateProvider, $urlRouterProvider, $locationProvider){
	$urlRouterProvider.otherwise('/');   

	$stateProvider
		.state('home', {
			url: '/',
			templateUrl: '../views/main.html'   
		}) 
    // GoogleBot SEO
    $locationProvider.html5Mode(true); 
})
angular.module('MainHeader',[]); 
angular.module('MainHeader')
.directive('headerContent',function(){ 
	return {
		replace: true,
		transclude: true,
		controller: function(){
			var HC = this;
			HC.name = "Drew Peterson";
			HC.title = "FrontEnd Developer";
			HC.img = "https://lh3.googleusercontent.com/rdg7kc1Uftgsg8xLiNcCYljQ52mvepBA1j-JHJpsAiIq0qu5L3k6D4GWkj1QxEYKtkyoVTB_m4E=w5120-h3200-rw-no";
		},
		controllerAs: 'HC',
		templateUrl: '../views/headerContent.html' 
	}
})
angular.module('MainHeader')
.directive('headerSocial',function(){ 
	return {
		replace: true,
		controller: function(){
			var HS = this;
			HS.soicalIcons = ['E','P','G','L'];
		},
		controllerAs: 'HS',
		templateUrl: '../views/headerSocial.html' 
	}
})
angular.module('MainHeader')
.directive('mainHeader',function(){ 
	return {
		replace: true,
		transclude: true,
		controller: function(){
			var MH = this;
		},
		controllerAs: 'MH',
		templateUrl: '../views/mainHeader.html' 
	}
})
